//uzik_kadastr_popup action trigger
window.init_popup = async function()
{
	var fi = altrpHelpers.getDataByPath('altrppagestate.popup_info');
	altrpHelpers.setDataByPath('altrppagestate.in_wait', 1);
	altrpHelpers.setDataByPath('altrppagestate.autocomplete_last_value', null);
	RaiseActions('_prepare_ds_for_secondary_');	
	if (!fi?.doc?.uzik_arenda_id && !!fi?.doc?.id){
		await UpdateDataSource('sf_list');
		await UpdateDataSource('okrug_list');
		await UpdateDataSource('rn_list');
		await UpdateDataSource('predp_list');
		CorrectMultiSelectWidget('subject_rf_json_array');
		CorrectMultiSelectWidget('okrug_json_array');
		CorrectMultiSelectWidget('rn_json_array');
		CorrectMultiSelectWidget('predp_json_array');
	}
	var ui = altrpHelpers.getDataByPath('altrpuser');
	if (fi.doc.id)
		setPageParams('id', fi.doc.id);
	else
		setPageParams('id', null);
	/*if ((fi.mode == 'edit' && !(ui.id == fi.doc.creator_id || ui.id == fi.doc.engineer_id))
		|| last_phyz.actual_id > 0)
		altrpHelpers.setDataByPath('altrppagestate.popup_info.mode', 'show');*/

	InitPopupFunctions({
		validate: async function(){
			var ret = [];
			var f = altrpHelpers.getDataByPath('altrpforms.edit_form');
			f.kadastr_number = f.kadastr_number?.trim();
			f.egrn_number = f.egrn_number?.trim();
			f.cert_num = f.cert_num?.trim();		
			/*if (f.kadastr_owner == 1 && !f.file_id && !altrpHelpers.getDataByPath('altrppagestate.popup_info.doc.file_on_network_drive'))
				ret.push('Загрузите сканированное изображение документа');
			else */if (f.kadastr_number?.length > 0 && f.kadastr_number?.trim() == f.kadastr_number_zu?.trim())
				ret.push('Кадастровые номера объекта и земельного участка не могут совпадать.');
			else {
				var r = await UpdateDataSource('check_kadastr_unique');
				if (!!r?.msg)
					ret.push(r.msg);				
			}
			//else if (!f.egrn_date)
			//	ret.push('Дата ЕГРН не указана. Вы уверены что хотите продолжить?');
			return ret;
		},
		afterChange: async function(button, id){
			var f = altrpHelpers.getDataByPath('altrpforms.edit_form');
			if (button == 'save' || button == 'duplicate'){
				setPageParams('id', null);
			}
			else if (button == 'add_and_continue'){
				alert('Новая кадастровая карточка успешно добавлена.');
			}
			if (button == 'duplicate' || button == 'add_and_continue'){
				altrpHelpers.setDataByPath('altrpforms.edit_form.kadastr_number', null);
				altrpHelpers.setDataByPath('altrpforms.edit_form.inv_numbers', null);
				altrpHelpers.setDataByPath('altrpforms.edit_form.egrn_number', null);
				altrpHelpers.setDataByPath('altrpforms.edit_form.cert_num', null);
				altrpHelpers.setDataByPath('altrpforms.edit_form.egrn_date', null);
				altrpHelpers.setDataByPath('altrpforms.edit_form.file_id', null);
				altrpHelpers.setDataByPath('altrpforms.edit_form.file_on_network_drive', null);
				altrpHelpers.setDataByPath('altrppagestate.popup_info.doc', null);
			}
			if (f.uzik_arenda_id > 0)
				await UpdateDataSource('kadastrs');
			//Рефреш таблицы
			window.on_show_loader(1);
			for(var t in window.altdata?.tables)
				await window.altdata?.tables[t].updateDataWithCurrentQuery();
			window.on_show_loader(0);
		}
	});
	altrpHelpers.setDataByPath('altrppagestate.in_wait', 0);
};

